create or replace view V_TEKLI_BP as
SELECT T.USERID,
           T.MAXDATE,
           T.SEGMENT_ASSIGNED,
           T.ENSONYATIRILANBONUS,
           T.CIRO,
           T.DESCRIPTION,
           T.ONGORULENBP,
           AT2.BITISTAR,
           T.CIRO_SON12AY,
           T.BONUSKULLANIM_SON12AY,
           T.TJK_CIRO_SON12AY,
           T.TJK_BONUSKULLANIM_SON12AY,
           SYSDATE     MODIFIED_DATE
      FROM (SELECT T.USERID,
                   T.MAXDATE,
                   T.SEGMENT_ASSIGNED,
                   T.ENSONYATIRILANBONUS,
                   T.CIRO,
                   T.DESCRIPTION,
                   T.ONGORULENBP,
                   T.CIRO_SON12AY,
                   T.BONUSKULLANIM_SON12AY,
                   T.TJK_CIRO_SON12AY,
                   T.TJK_BONUSKULLANIM_SON12AY
              FROM (  SELECT USERID,
                             DESCRIPTION,
                             MAXDATE,
                             SEGMENT_ASSIGNED,
                             TO_CHAR (YATIRILANBONUS)    ENSONYATIRILANBONUS,
                             SUM (C2.NAMOUNT)            AS CIRO,
                               CEIL (
                                     CASE
                                         WHEN SEGMENT_ASSIGNED = 'A0'
                                         THEN
                                             SUM (C2.NAMOUNT) * 0.003
                                         WHEN SEGMENT_ASSIGNED = 'A1'
                                         THEN
                                             SUM (C2.NAMOUNT) * 0.002
                                         WHEN SEGMENT_ASSIGNED = 'A2'
                                         THEN
                                             SUM (C2.NAMOUNT) * 0.001
                                         WHEN SEGMENT_ASSIGNED = 'A3'
                                         THEN
                                             SUM (C2.NAMOUNT) * 0.0004
                                     END
                                   / 10)
                             * 10                        ONGORULENBP,
                             CIRO_SON12AY,
                             BONUSKULLANIM_SON12AY,
                             0                           TJK_CIRO_SON12AY,
                             0                           TJK_BONUSKULLANIM_SON12AY
                        FROM (  SELECT Y.CID,
                                       USERID,
                                       SEGMENT_ASSIGNED,
                                       YATIRILANBONUS,
                                       DESCRIPTION,
                                       MAX (ODATE)
                                           AS MAXDATE,
                                       MIN (ODATE)
                                           AS MINDATE,
                                       MAX (ODATE) - MIN (ODATE)
                                           AS DIF_DAY,
                                       CIRO_SON12AY,
                                       BONUSKULLANIM_SON12AY,
                                       0
                                           TJK_CIRO_SON12AY,
                                       0
                                           TJK_BONUSKULLANIM_SON12AY
                                  FROM (  SELECT X.CID
                                                     AS CID,
                                                 BC.USERID
                                                     AS USERID,
                                                 X.ODATE
                                                     AS ODATE,
                                                 X.RW
                                                     AS RW,
                                                 X.YATIRILANBONUS,
                                                 SEGMENT_ASSIGNED,
                                                 DESCRIPTION,
                                                 BC.CIRO_SON12AY
                                                     AS CIRO_SON12AY,
                                                 BC.BONUSKULLANIM_SON12AY
                                                     AS BONUSKULLANIM_SON12AY,
                                                 0
                                                     AS TJK_CIRO_SON12AY,
                                                 0
                                                     AS TJK_BONUSKULLANIM_SON12AY
                                            FROM (  SELECT MAX (ODATE)
                                                               ODATE,
                                                           ROW_NUMBER ()
                                                               OVER (
                                                                   PARTITION BY CUSTOMER_ID
                                                                   ORDER BY
                                                                       TRUNC (ODATE) DESC)
                                                               AS RW,
                                                           RBN.AMOUNT
                                                               YATIRILANBONUS,
                                                           RBN.DESCRIPTION
                                                               DESCRIPTION,
                                                           CUSTOMER_ID
                                                               AS CID
                                                      FROM LBRDWH.BONUS_NEW RBN
                                                     WHERE     RBN.CAMPAIGN_ID NOT IN
                                                                   ('2789',
                                                                    '2805',
                                                                    '2818',
                                                                    '2807')
                                                           AND RBN.CUSTOMER_ID NOT IN
                                                                   (SELECT CUSTOMER_ID
                                                                      FROM LBRDWH.CUSTOMER_RESTRICTED_BONUS
                                                                           CRB)
                                                  GROUP BY CUSTOMER_ID,
                                                           AMOUNT,
                                                           ODATE,
                                                           DESCRIPTION) X
                                                 JOIN CRM_DM.BONUS_CALISMA BC
                                                     ON     X.CID = BC.CUSTOMER_ID
                                                        AND BC.SEGMENT_ASSIGNED IN
                                                                ('A0',
                                                                 'A1',
                                                                 'A2',
                                                                 'A3')
                                           WHERE 1 = 1 AND RW = 1
                                        GROUP BY X.CID,
                                                 BC.USERID,
                                                 X.ODATE,
                                                 DESCRIPTION,
                                                 X.YATIRILANBONUS,
                                                 X.RW,
                                                 SEGMENT_ASSIGNED,
                                                 CIRO_SON12AY,
                                                 BONUSKULLANIM_SON12AY
                                        ORDER BY X.CID) Y
                              GROUP BY CID,
                                       USERID,
                                       YATIRILANBONUS,
                                       SEGMENT_ASSIGNED,
                                       DESCRIPTION,
                                       CIRO_SON12AY,
                                       BONUSKULLANIM_SON12AY) Z
                             LEFT JOIN CRM_DM.TS_CUSTOMER_DAILY_AMOUNT C2
                                 ON     Z.CID = C2.NCUSTOMER_ID
                                    AND C2.DPLAY_DATE BETWEEN TRUNC (
                                                                  Z.MAXDATE + 1)
                                                          AND TRUNC (SYSDATE)
                    GROUP BY USERID,
                             MAXDATE,
                             YATIRILANBONUS,
                             MINDATE,
                             DESCRIPTION,
                             SEGMENT_ASSIGNED,
                             CIRO_SON12AY,
                             BONUSKULLANIM_SON12AY
                    UNION ALL
                      SELECT USERID,
                             'HI KAMPANYA YOK',
                             TO_DATE ('2099/01/01', 'YYYY/MM/DD'),
                             SEGMENT_ASSIGNED,
                             'ENSONYATIRILANBONUS',
                             SUM (C2.NAMOUNT)    AS CIRO,
                               CEIL (
                                     (CASE
                                          WHEN SEGMENT_ASSIGNED = 'A0'
                                          THEN
                                              SUM (C2.NAMOUNT) * 0.004
                                          WHEN SEGMENT_ASSIGNED = 'A1'
                                          THEN
                                              SUM (C2.NAMOUNT) * 0.002
                                          WHEN SEGMENT_ASSIGNED = 'A2'
                                          THEN
                                              SUM (C2.NAMOUNT) * 0.001
                                          WHEN SEGMENT_ASSIGNED = 'A3'
                                          THEN
                                              SUM (C2.NAMOUNT) * 0.0004
                                      END)
                                   / 10)
                             * 10                ONGORULENBP,
                             CIRO_SON12AY,
                             BONUSKULLANIM_SON12AY,
                             0                   TJK_CIRO_SON12AY,
                             0                   TJK_BONUSKULLANIM_SON12AY
                        FROM (SELECT BC.CUSTOMER_ID
                                         CID,
                                     BC.USERID
                                         USERID,
                                     BC.SEGMENT_ASSIGNED
                                         SEGMENT_ASSIGNED,
                                     BN.ODATE,
                                     BC.CIRO_SON12AY
                                         AS CIRO_SON12AY,
                                     BC.BONUSKULLANIM_SON12AY
                                         AS BONUSKULLANIM_SON12AY,
                                     0
                                         AS TJK_CIRO_SON12AY,
                                     0
                                         AS TJK_BONUSKULLANIM_SON12AY
                                FROM CRM_DM.BONUS_CALISMA BC
                                     LEFT JOIN LBRDWH.BONUS_NEW BN
                                         ON BC.CUSTOMER_ID = BN.CUSTOMER_ID
                               WHERE     BC.SEGMENT_ASSIGNED IN ('A0',
                                                                 'A1',
                                                                 'A2',
                                                                 'A3')
                                     AND BN.ODATE IS NULL) X
                             JOIN CRM_DM.TS_CUSTOMER_DAILY_AMOUNT C2
                                 ON C2.NCUSTOMER_ID = X.CID
                       WHERE C2.DPLAY_DATE > TRUNC (SYSDATE - 30)
                    GROUP BY CID,
                             SEGMENT_ASSIGNED,
                             USERID,
                             CIRO_SON12AY,
                             BONUSKULLANIM_SON12AY) T) T
           LEFT JOIN CRM_DM.ARAMALAR_TEKLIFLER AT2
               ON AT2.USERID = T.USERID AND AT2.BITISTAR = DATE '2039-12-31'
/

