    procedure p_bc_update_08 is
    begin
        update /*+ parallel(4) */ crm_DM.Bonus_Calisma_tmp
                    set Para_Bonus_Kontrol=case
                    when NVL(Ciro_Son12Ay,0)=0 then -10
                    when NVL(SonParaTransferiUzerindenGecenSure,400)=400 then -9
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.07 and NVL(SonParaTransferiUzerindenGecenSure,400)-NVL(SonBonusYuklemeUzerindenGecenSure,400)>0 then -8
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.05 and NVL(SonParaTransferiUzerindenGecenSure,400)-NVL(SonBonusYuklemeUzerindenGecenSure,400)>0 then -7
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.07 and NVL(SonBonusYuklemeUzerindenGecenSure,400)<10 then -6
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.05 and NVL(SonBonusYuklemeUzerindenGecenSure,400)<10 then -5
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.07 and NVL(SonBonusYuklemeUzerindenGecenSure,400)<30 then -4
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.07 and NVL(SonParaTransferiUzerindenGecenSure,400)-NVL(SonBonusYuklemeUzerindenGecenSure,400)<=0 then -3
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.05 and NVL(SonBonusYuklemeUzerindenGecenSure,400)<30 then -2
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)>0.05 and NVL(SonParaTransferiUzerindenGecenSure,400)-NVL(SonBonusYuklemeUzerindenGecenSure,400)<=0 then -1
                    when NVL(Ciro_Son12Ay,0)>0 and NVL(BonusKullanm_Son12Ay,0)*1.0/NVL(Ciro_Son12Ay,0)<=0.05 then
                        case
                        when NVL(SonParaTransferiUzerindenGecenSure,400)<=10 then
                            case
                            when NVL(SonBonusYuklemeUzerindenGecenSure,400)<=10 then 0
                            when NVL(SonBonusYuklemeUzerindenGecenSure,400)<=30 then 1
