                pBUGUN  DATE:=TRUNC(SYSDATE);
                pGUN    DATE;
                pSONGUN DATE;
                pAYLIK_YUKLEME_SAYI number;
                pMax_OngorulenYuzde_for_low_Bonus number(22,6):=0.0025;
                pBASLANGIC_TAR DATE:=TRUNC(SYSDATE);
