                pGUN :=trunc(last_day(pBUGUN)-1, 'mm');
                pSONGUN :=trunc(last_day(pBUGUN));
                 begin
                 select ceil(sum(case when GUN in ('Friday','Saturday') then 1 else 0 end)*1.0/2)
                 into pAYLIK_YUKLEME_SAYI
                 from crm_dm.Ay_Gunler where TARIH between pGUN and pSONGUN
                 ;
                 exception when no_data_found or too_many_rows then
                   pAYLIK_YUKLEME_SAYI := 0;
                end;

                MERGE /*+ parallel(4) */ into CRM_DM.BONUS_CALISMA_tmp A
                USING crm_DM.ARAMALAR_TEKLIFLER B
                ON (A.USERID=B.USERID)
                WHEN MATCHED THEN UPDATE
                set
                OngorulenBonus= case
                when (NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI-OngorulenBonus>0
                then
                    case
                    when (NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI<=30
                            then round((NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI/5,0)*5
                    when (NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI<=250
                            then round((NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI/10,0)*10
                    when (NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI<=500
                            then round((NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI/20,0)*20
                    when (NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI<=1000
                            then round((NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI/25,0)*25
                    else
