                when (NVL(Ciro_SonAy,0)-NVL(BonusKullanm_SonAy,0))*pMax_OngorulenYuzde_for_low_Bonus/pAYLIK_YUKLEME_SAYI-OngorulenBonus>0
                then 'Teklif-Dusuk bonuslar icin duzeltme'
                else OngorulenBonusNeden end
